import React from 'react';
import SiteNavbar from './components/Navbar';     // 上部ナビ
import BigHeading from './components/BigHeading'; // 大きな見出し
import ProductGrid from './components/ProductGrid'; // 3列カード
import SidebarExtras from './components/SidebarExtras'; // 検索ボックス/スポンサーリンク
import Footer from './components/Footer';

function App() {
  return (
    <>
      <SiteNavbar />
      
      {/* 大きな見出し */}
      <BigHeading text="背景が動く" />

      <div className="container mt-4">
        <div className="row">
          {/* 左カラム: 3列カード */}
          <div className="col-12 col-lg-8">
            <ProductGrid />
          </div>

          {/* 右カラム: 検索フォームやスポンサーリンク */}
          <div className="col-12 col-lg-4 mt-3 mt-lg-0">
            <SidebarExtras />
          </div>
        </div>
      </div>

      <Footer />
    </>
  );
}

export default App;
