import React, { useEffect, useState } from 'react';
import { Card, Button } from 'react-bootstrap';

type Product = {
  id: number;
  name: string;
  stock: number;
  price: number;
  imageUrl: string;
};

function ProductList() {
  const [products, setProducts] = useState<Product[]>([]);

  useEffect(() => {
    // ダミーデータ (6つ、3列表示)
    // col-lg-4 で大画面時に1行3列
    const mockData: Product[] = [
      {
        id: 1,
        name: 'カステラ',
        stock: 10,
        price: 1000,
        imageUrl: '/images/castella.png'
      },
      {
        id: 2,
        name: 'いちごマリトッツオ',
        stock: 5,
        price: 2000,
        imageUrl: '/images/maritozzo.png'
      },
      {
        id: 3,
        name: 'チョコレートケーキ',
        stock: 8,
        price: 700,
        imageUrl: '/images/chocolate_cake.png'
      },
      {
        id: 4,
        name: 'ショートケーキ',
        stock: 3,
        price: 500,
        imageUrl: '/images/shortcake.png'
      },
      {
        id: 5,
        name: 'コーヒー',
        stock: 20,
        price: 300,
        imageUrl: '/images/coffee.png'
      },
      {
        id: 6,
        name: '紅茶',
        stock: 15,
        price: 250,
        imageUrl: '/images/tea.png'
      }
    ];
    setProducts(mockData);
  }, []);

  const addToCart = (productId: number) => {
    const currentCart = localStorage.getItem('cart');
    const cartData: { productId: number; quantity: number }[] =
      currentCart ? JSON.parse(currentCart) : [];

    const existingIndex = cartData.findIndex(
      (item) => item.productId === productId
    );

    if (existingIndex >= 0) {
      cartData[existingIndex].quantity += 1;
    } else {
      cartData.push({ productId, quantity: 1 });
    }

    localStorage.setItem('cart', JSON.stringify(cartData));
    alert('カートに追加: ' + productId);
  };

  return (
    <div className="row">
      {products.map((p) => {
        // コメントは関数ブロック内で
        // 大画面(992px以上): col-lg-4 で3列
        // 中画面(768px以上): col-md-4 で3列
        // 小画面(576px以上): col-sm-6 で2列
        return (
          <div key={p.id} className="col-sm-6 col-md-4 col-lg-4 mb-3">
            <Card>
              <Card.Img
                variant="top"
                src={p.imageUrl}
                style={{ objectFit: 'cover', height: '200px' }}
              />
              <Card.Body>
                <Card.Title>{p.name}</Card.Title>
                <Card.Text>在庫: {p.stock}</Card.Text>
                <Card.Text>価格: {p.price}円</Card.Text>
                <Button
                  variant="primary"
                  onClick={() => addToCart(p.id)}
                  disabled={p.stock === 0}
                >
                  カートに入れる
                </Button>
              </Card.Body>
            </Card>
          </div>
        );
      })}
    </div>
  );
}

export default ProductList;
