import React, { useEffect, useState } from 'react';
import { Card, Button } from 'react-bootstrap';

type Product = {
  id: number;
  name: string;
  stock: number;
  price: number;
  imageUrl: string;
};

function ProductList() {
  const [products, setProducts] = useState<Product[]>([]);

  useEffect(() => {
    // APIから取得する想定。サンプルとしてダミーを設定
    const mockData: Product[] = [
      {
        id: 1,
        name: '商品A',
        stock: 10,
        price: 1000,
        imageUrl: 'https://via.placeholder.com/200'
      },
      {
        id: 2,
        name: '商品B',
        stock: 5,
        price: 2000,
        imageUrl: 'https://via.placeholder.com/200'
      }
    ];
    setProducts(mockData);
  }, []);

  const addToCart = (productId: number) => {
    const currentCart = localStorage.getItem('cart');
    const cartData: { productId: number; quantity: number }[] =
      currentCart ? JSON.parse(currentCart) : [];

    const existingIndex = cartData.findIndex(
      (item) => item.productId === productId
    );

    if (existingIndex >= 0) {
      cartData[existingIndex].quantity += 1;
    } else {
      cartData.push({ productId, quantity: 1 });
    }

    localStorage.setItem('cart', JSON.stringify(cartData));
    alert('カートに追加: ' + productId);
  };

  return (
    <div className="row">
      {products.map((p) => (
        <div key={p.id} className="col-sm-6 col-md-4 col-lg-6 mb-3">
          <Card>
            <Card.Img
              variant="top"
              src={p.imageUrl}
              style={{ objectFit: 'cover', height: '200px' }}
            />
            <Card.Body>
              <Card.Title>{p.name}</Card.Title>
              <Card.Text>在庫: {p.stock}</Card.Text>
              <Card.Text>価格: {p.price}円</Card.Text>
              <Button
                variant="primary"
                onClick={() => addToCart(p.id)}
                disabled={p.stock === 0}
              >
                カートに入れる
              </Button>
            </Card.Body>
          </Card>
        </div>
      ))}
    </div>
  );
}

export default ProductList;
