import React, { useEffect, useState } from 'react';
import { Card, Button } from 'react-bootstrap';

type Product = {
  id: number;
  name: string;
  stock: number;
  price: number;
  imageUrl: string;
  category: 'food' | 'drink'; // 追加
};

// 受け取るpropsの型
type ProductListProps = {
  selectedCategory: string; // "All" | "food" | "drink"
};

function ProductList({ selectedCategory }: ProductListProps) {
  const [products, setProducts] = useState<Product[]>([]);

  useEffect(() => {
    const mockData: Product[] = [
      {
        id: 1,
        name: 'カステラ',
        stock: 10,
        price: 1000,
        imageUrl: '/images/castella.png',
        category: 'food'
      },
      {
        id: 2,
        name: 'いちごマリトッツオ',
        stock: 5,
        price: 2000,
        imageUrl: '/images/maritozzo.png',
        category: 'food'
      },
      {
        id: 3,
        name: 'チョコレートケーキ',
        stock: 8,
        price: 700,
        imageUrl: '/images/chocolate_cake.png',
        category: 'food'
      },
      {
        id: 4,
        name: 'ショートケーキ',
        stock: 3,
        price: 500,
        imageUrl: '/images/shortcake.png',
        category: 'food'
      },
      {
        id: 5,
        name: 'コーヒー',
        stock: 20,
        price: 300,
        imageUrl: '/images/coffee.png',
        category: 'drink'
      },
      {
        id: 6,
        name: '紅茶',
        stock: 15,
        price: 250,
        imageUrl: '/images/tea.png',
        category: 'drink'
      }
    ];
    setProducts(mockData);
  }, []);

  // カテゴリでフィルタ
  const filteredProducts =
    selectedCategory === 'All'
      ? products
      : products.filter((p) => p.category === selectedCategory);

  const addToCart = (productId: number) => {
    const currentCart = localStorage.getItem('cart');
    const cartData: { productId: number; quantity: number }[] =
      currentCart ? JSON.parse(currentCart) : [];

    const existingIndex = cartData.findIndex((item) => item.productId === productId);
    if (existingIndex >= 0) {
      cartData[existingIndex].quantity += 1;
    } else {
      cartData.push({ productId, quantity: 1 });
    }

    localStorage.setItem('cart', JSON.stringify(cartData));
    alert('カートに追加: ' + productId);
  };

  return (
    <div className="row">
      {filteredProducts.map((p) => {
        return (
          <div key={p.id} className="col-sm-6 col-md-4 col-lg-4 mb-3">
            <Card>
              <Card.Img
                variant="top"
                src={p.imageUrl}
                style={{ objectFit: 'cover', height: '200px' }}
              />
              <Card.Body>
                <Card.Title>{p.name}</Card.Title>
                <Card.Text>在庫: {p.stock}</Card.Text>
                <Card.Text>価格: {p.price}円</Card.Text>
                <Button
                  variant="primary"
                  onClick={() => addToCart(p.id)}
                  disabled={p.stock === 0}
                >
                  カートに入れる
                </Button>
              </Card.Body>
            </Card>
          </div>
        );
      })}
    </div>
  );
}

export default ProductList;
